import React, { useEffect, useState } from "react";
import "./CommentSection.css";

const CommentSection = ({ productId }) => {
    const [userName, setUserName] = useState("");
    const [comment, setComment] = useState("");
    const [comments, setComments] = useState([]);
    const [rating, setRating] = useState(5);
    const [message, setMessage] = useState("");
    const [loading, setLoading] = useState(false);
    const token = localStorage.getItem("token");

    // Fetch User Data
    useEffect(() => {
        const fetchUserData = async () => {
            if (!token) return;
            try {
                const response = await fetch(`http://localhost:3300/user?token=${token}`);
                const data = await response.json();
                if (response.ok) {
                    setUserName(data.user?.name || "Anonymous");
                } else {
                    setMessage(data.message || "Failed to load user data.");
                }
            } catch (error) {
                setMessage("Error fetching user data.");
            }
        };
        fetchUserData();
    }, [token]);

    // Fetch Comments
    const fetchComments = async () => {
        try {
            const response = await fetch(`http://localhost:3300/product/comment/${productId}`);
            const data = await response.json();
            console.log("Fetched comments from DB:", data); // Debugging

            if (Array.isArray(data)) {
                setComments([...data].reverse());
            } else {
                setComments([]);
            }
        } catch (error) {
            console.error("Fetch error:", error);
            setMessage("Error fetching comments.");
        }
    };

    useEffect(() => {
        fetchComments();
    }, [productId]);

    // Handle Comment Submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!userName || !comment || !rating) {
            setMessage("All fields are required!");
            return;
        }
        setLoading(true);

        try {
            const response = await fetch(`http://localhost:3300/product/comment/${productId}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ userName, comment, rating }),
            });

            const data = await response.json();
            if (response.ok) {
                setComment("");
                setRating(5);
                setMessage("");

                // Re-fetch comments from the database after submitting
                fetchComments();
            } else {
                setMessage(data.message || "Failed to add comment.");
            }
        } catch (error) {
            setMessage("Error submitting comment.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="comment-section">
            <h3>{comments.length} Comments</h3>
            <div className="comment-input-box">
                <form onSubmit={handleSubmit} className="comment-form">
                    <textarea
                        placeholder="Add a comment..."
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        required
                    />
                    <button type="submit" disabled={loading}>
                        {loading ? "Submitting..." : "Comment"}
                    </button>
                </form>
            </div>

            {message && <p className="comment-message">{message}</p>}

            <ul className="comment-list">
                {comments.length > 0 ? (
                    comments.map((c, index) => (
                        <li key={index} className="comment-item">
                            <div className="comment-content">
                                <strong>@{c.userName || "Unknown"}</strong>
                                <span className="comment-timestamp">{c.timestamp || "Just now"}</span>
                                <p>{c.comment}</p>
                                <div className="comment-actions">
                                    <span>üëç {c.likes || 0}</span>
                                    <span>üí¨ Reply</span>
                                </div>
                            </div>
                        </li>
                    ))
                ) : (
                    <p>No comments yet.</p>
                )}
            </ul>
        </div>
    );
};

export default CommentSection;
