import React, { useEffect, useState } from "react";
import "./CommentForm.css";

const CommentForm = ({ productId }) => {
    const [userName, setUserName] = useState("");
    const [comment, setComment] = useState("");
    const [rating, setRating] = useState(5);
    const [message, setMessage] = useState("");
    const [loading, setLoading] = useState(false);

    // Fetch user data when the component mounts
    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem("token");
            if (!token) return;

            try {
                const response = await fetch(`http://localhost:3300/user?token=${token}`);
                const data = await response.json();

                if (response.ok) {
                    setUserName(data.user.name || "Anonymous"); // Auto-fill username
                } else {
                    setMessage(data.message || "Failed to load user data.");
                }
            } catch (error) {
                setMessage("Error fetching user data");
            }
        };

        fetchUserData();
    }, [token]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!userName || !comment || !rating) {
            setMessage("All fields are required!");
            return;
        }

        setLoading(true);

        try {
            const response = await fetch(`http://localhost:3300/product/comment/${productId}`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ userName, comment, rating }),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage("Comment added successfully!");
                setComment("");
                setRating(5);
            } else {
                setMessage(data.message || "Failed to add comment.");
            }
        } catch (error) {
            setMessage("Error submitting comment.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="comment-form">
            <h3>Leave a Comment</h3>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={userName}
                    readOnly
                    disabled
                    placeholder="Your Name"
                />
                <textarea
                    placeholder="Write your comment..."
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    required
                />
                <select value={rating} onChange={(e) => setRating(Number(e.target.value))}>
                    <option value={5}>⭐️⭐️⭐️⭐️⭐️ (5)</option>
                    <option value={4}>⭐️⭐️⭐️⭐️ (4)</option>
                    <option value={3}>⭐️⭐️⭐️ (3)</option>
                    <option value={2}>⭐️⭐️ (2)</option>
                    <option value={1}>⭐️ (1)</option>
                </select>
                <button type="submit" disabled={loading}>
                    {loading ? "Submitting..." : "Submit Comment"}
                </button>
            </form>
        </div>
    );
};

export default CommentForm;
