import React, { useEffect, useState } from "react";
import "./CommentSection.css";

const CommentSection = ({ productId }) => {
    const [userName, setUserName] = useState("");
    const [comment, setComment] = useState("");
    const [comments, setComments] = useState([]);
    const [rating, setRating] = useState(5);
    const [message, setMessage] = useState("");
    const [loading, setLoading] = useState(false);
    const [token, setToken] = useState(localStorage.getItem("token"));
    const [replyText, setReplyText] = useState("");

    useEffect(() => {
        const fetchUserData = async () => {
            if (!token) return;
            try {
                const response = await fetch(`http://localhost:3300/user?token=${token}`);
                const data = await response.json();
                if (response.ok) {
                    setUserName(data.user.name || "Anonymous");
                } else {
                    setMessage(data.message || "Failed to load user data.");
                }
            } catch (error) {
                setMessage("Error fetching user data");
            }
        };
        fetchUserData();
    }, [token]);

    useEffect(() => {
        const fetchComments = async () => {
            try {
                const response = await fetch(`http://localhost:3300/product/comment/${productId}`);
                const data = await response.json();
                console.log("Fetched comments from DB:", data);

                if (data && typeof data === "object") {
                    setComments(Object.entries(data).map(([id, obj]) => ({ id, ...obj })).reverse());
                } else {
                    setComments([]);
                }
            } catch (error) {
                console.error("Fetch error:", error);
                setMessage("Error fetching comments.");
            }
        };

        fetchComments();
    }, [productId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!userName || !comment || !rating) {
            setMessage("All fields are required!");
            return;
        }
        setLoading(true);
        try {
            const response = await fetch(`http://localhost:3300/product/comment/${productId}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ userName, comment, rating }),
            });

            const data = await response.json();
            if (response.ok) {
                setComments([{ id: data.comment.id, ...data.comment }, ...comments]);
                setComment("");
                setRating(5);
                setMessage("");
            } else {
                setMessage(data.message || "Failed to add comment.");
            }
        } catch (error) {
            setMessage("Error submitting comment.");
        } finally {
            setLoading(false);
        }
    };

    const handleLike = async (commentId) => {
        try {
            await fetch(`http://localhost:3300/product/comment/${productId}/like/${commentId}`, { method: "POST" });
            setComments(comments.map(c => c.id === commentId ? { ...c, likes: c.likes + 1 } : c));
        } catch (error) {
            console.error("Error liking comment:", error);
        }
    };

    const handleReply = async (commentId) => {
        if (!replyText) return;
        try {
            const response = await fetch(`http://localhost:3300/product/comment/${productId}/reply/${commentId}`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ userName, reply: replyText }),
            });

            if (response.ok) {
                setComments(comments.map(c => 
                    c.id === commentId ? { ...c, replies: [...c.replies, { userName, reply: replyText, timestamp: "Just now" }] } : c
                ));
                setReplyText("");
            }
        } catch (error) {
            console.error("Error adding reply:", error);
        }
    };

    return (
        <div className="comment-section">
            <h3>{comments.length} Comments</h3>
            <form onSubmit={handleSubmit} className="comment-form">
                <textarea placeholder="Add a comment..." value={comment} onChange={(e) => setComment(e.target.value)} required />
                <button type="submit" disabled={loading}>{loading ? "Submitting..." : "Comment"}</button>
            </form>
            {message && <p className="comment-message">{message}</p>}

            <ul className="comment-list">
                {comments.map((c) => (
                    <li key={c.id} className="comment-item">
                        <div className="comment-content">
                            <strong>@{c.userName}</strong> <span className="comment-timestamp">{c.timestamp}</span>
                            <p>{c.comment}</p>
                            <div className="comment-actions">
                                <button onClick={() => handleLike(c.id)}>üëç {c.likes}</button>
                                <input type="text" placeholder="Reply..." onChange={(e) => setReplyText(e.target.value)} />
                                <button onClick={() => handleReply(c.id)}>üí¨ Reply</button>
                            </div>
                            <ul>
                                {c.replies?.map((r, i) => <li key={i}><strong>@{r.userName}</strong>: {r.reply}</li>)}
                            </ul>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CommentSection;
