import React, { useState } from "react";
import "./CommentForm.css";
const CommentForm = ({ productId }) => {
    const [userName, setUserName] = useState("");
    const [comment, setComment] = useState("");
    const [rating, setRating] = useState(5);
    const [message, setMessage] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!userName || !comment || !rating) {
            setMessage("All fields are required!");
            return;
        }

        try {
            const response = await fetch(`http://localhost:3300/products/${productId}/comment`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ userName, comment, rating }),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage("Comment added successfully!");
                setUserName("");
                setComment("");
                setRating(5);
            } else {
                setMessage(data.message || "Failed to add comment.");
            }
        } catch (error) {
            setMessage("Error submitting comment.");
        }
    };

    return (
        <div className="comment-form">
            <h3>Leave a Comment</h3>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Your Name"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Write your comment..."
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    required
                />
                <select value={rating} onChange={(e) => setRating(Number(e.target.value))}>
                    <option value={5}>⭐️⭐️⭐️⭐️⭐️ (5)</option>
                    <option value={4}>⭐️⭐️⭐️⭐️ (4)</option>
                    <option value={3}>⭐️⭐️⭐️ (3)</option>
                    <option value={2}>⭐️⭐️ (2)</option>
                    <option value={1}>⭐️ (1)</option>
                </select>
                <button type="submit">Submit Comment</button>
            </form>
        </div>
    );
};

export default CommentForm;
